import CardProduct3 from "./CardProduct3";
import React, { Component } from "react";
import Slider from "react-slick";

export default class Responsive extends Component {
  render() {
    var settings = {
      dots: true,
      infinite: false,
      speed: 500,
      slidesToShow: 5.2,
      slidesToScroll: 4,
      initialSlide: 0,
      responsive: [
        {
          breakpoint: 1440,
          settings: {
            slidesToShow: 5.2,
            slidesToScroll: 5,
            infinite: true,
            dots: true
          }
        },
        {
          breakpoint: 1430,
          settings: {
            slidesToShow: 5,
            slidesToScroll: 5,
            infinite: true,
            dots: true
          }
        },
        {
          breakpoint: 1380,
          settings: {
            slidesToShow: 4.8,
            slidesToScroll: 4,
            infinite: true,
            dots: true
          }
        },
        {
          breakpoint: 1320,
          settings: {
            slidesToShow: 4.5,
            slidesToScroll: 4,
            infinite: true,
            dots: true
          }
        },
        {
          breakpoint: 1230,
          settings: {
            slidesToShow: 4.2,
            slidesToScroll: 4,
            infinite: true,
            dots: true
          }
        },
        {
          breakpoint: 1200,
          settings: {
            slidesToShow: 4,
            slidesToScroll: 4,
            infinite: true,
            dots: true
          }
        },
        {
          breakpoint: 1024,
          settings: {
            slidesToShow: 3,
            slidesToScroll: 3,
            infinite: true,
            dots: true
          }
        },
        {
          breakpoint: 830,
          settings: {
            slidesToShow: 2.5,
            slidesToScroll: 2,
            initialSlide: 2
          }
        },
        {
          breakpoint: 768,
          settings: {
            slidesToShow: 2.5,
            slidesToScroll: 2,
            initialSlide: 2
          }
        },
        {
          breakpoint: 700,
          settings: {
            slidesToShow: 2.3,
            slidesToScroll: 2,
            initialSlide: 2
          }
        },
        {
          breakpoint: 640,
          settings: {
            slidesToShow: 2,
            slidesToScroll: 2,
            initialSlide: 2
          }
        },
        {
          breakpoint: 600,
          settings: {
            slidesToShow: 2,
            slidesToScroll: 2,
            initialSlide: 2
          }
        },
        {
          breakpoint: 570,
          settings: {
            slidesToShow: 1.7,
            slidesToScroll: 1
          }
      },
        {
            breakpoint: 540,
            settings: {
              slidesToShow: 1.5,
              slidesToScroll: 1
            }
        },
        {
          breakpoint: 480,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1
          }
        }
      ]
    };
    return (
      <div>
        <div className="banner2-control-1">
            <p>Featured</p>
            <button type="button" className="see-all">
                <p>See all  {" >>"}</p>
            </button>
        </div>
        <Slider {...settings}>
          <div className="Responsive-a">
            <CardProduct3/>
          </div>
          <div className="Responsive-a">
            <CardProduct3/>
          </div>
          <div className="Responsive-a">
            <CardProduct3/>
          </div>
          <div className="Responsive-a">
            <CardProduct3/>
          </div>
          <div className="Responsive-a">
            <CardProduct3/>
          </div>
          <div className="Responsive-a">
            <CardProduct3/>
          </div>
          <div className="Responsive-a">
            <CardProduct3/>
          </div>
          <div className="Responsive-a">
            <CardProduct3/>
          </div>
          <div className="Responsive-a">
            <CardProduct3/>
          </div>
          <div className="Responsive-a">
            <CardProduct3/>
          </div>
          <div className="Responsive-a">
            <CardProduct3/>
          </div>
        </Slider>
      </div>
    );
  }
}
