import CardProduct3 from "./CardProduct3";
import React, { Component } from "react";
import Slider from "react-slick";

export default class Responsive extends Component {
  render() {
    var settings = {
      dots: true,
      infinite: false,
      speed: 500,
      slidesToShow: 4.5,
      slidesToScroll: 4,
      initialSlide: 0,
      responsive: [
        {
            breakpoint: 1652,
            settings: {
              slidesToShow: 4.5,
              slidesToScroll: 4,
              infinite: true,
              dots: true
            }
        },
        {
          breakpoint: 1024,
          settings: {
            slidesToShow: 3,
            slidesToScroll: 3,
            infinite: true,
            dots: true
          }
        },
        {
          breakpoint: 768,
          settings: {
            slidesToShow: 2.5,
            slidesToScroll: 2,
            initialSlide: 2
          }
        },
        {
          breakpoint: 600,
          settings: {
            slidesToShow: 2,
            slidesToScroll: 2,
            initialSlide: 2
          }
        },
        {
            breakpoint: 540,
            settings: {
              slidesToShow: 1.5,
              slidesToScroll: 1
            }
        },
        {
          breakpoint: 480,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1
          }
        }
      ]
    };
    return (
      <div>
        <div className="banner2-control-1">
            <p>Featured</p>
            <button type="button" className="see-all">
                <p>See all  {" >>"}</p>
            </button>
        </div>
        <Slider {...settings}>
          <div className="Responsive-a">
            <CardProduct3/>
          </div>
          <div className="Responsive-a">
            <CardProduct3/>
          </div>
          <div className="Responsive-a">
            <CardProduct3/>
          </div>
          <div className="Responsive-a">
            <CardProduct3/>
          </div>
          <div className="Responsive-a">
            <CardProduct3/>
          </div>
          <div className="Responsive-a">
            <CardProduct3/>
          </div>
          <div className="Responsive-a">
            <CardProduct3/>
          </div>
          <div className="Responsive-a">
            <CardProduct3/>
          </div>
          <div className="Responsive-a">
            <CardProduct3/>
          </div>
          <div className="Responsive-a">
            <CardProduct3/>
          </div>
          <div className="Responsive-a">
            <CardProduct3/>
          </div>
        </Slider>
      </div>
    );
  }
}
